mapper.select:
select * from (select f.workerid,f.workeraccount,g.rolename,f.statename,f.worktime from (select c.workerid,c.workeraccount,c.statename,c.worktime,d.rolerid from (select a.workerid,a.workeraccount,a.worktime,b.statename from tbl_worker a,tbl_state b where a.stateid=b.stateid) c LEFT JOIN tbl_workeroler d on c.workerid=d.workerid) f LEFT JOIN tbl_role g on f.rolerid=g.roleid) a
and a.workeraccount=#{workeraccount}
LIMIT #{page},5

select a.deviceid devicid,a.devicearea,a.devicname,a.devictime,b.statename from tbl_devic a, tbl_state b a.devicstate=b.stateid
and a.devicname LIKE #{devicname}
limit #{page},5

mapper.update:
 update  tbl_workeroler
set   rolerid=#{rolerid}
  where workerid=#{workerid}

mapper.select:
select a.devicearea,a.devicname
from tbl_devic a
where a.devicearea=#{devicearea}
and a.devicname=#{devicname}

select * from tbl_combo a a.comboid=a.comboid
and a.combotime LIKE #{combotime}
limit #{page},5

select a.combotime
from tbl_combo a
where a.combotime=#{combotime}

select a.ruletime
from tbl_rules a
where a.ruletime=#{ruletime}

select * from tbl_log a a.logid=a.logid
and a.logname LIKE #{logname}
limit #{page},5
